    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int so1 = Integer.parseInt(khongbiet.getText());
        int so2 = Integer.parseInt(txtSo2.getText());
        int tong = so1 + so2;
        txtTong.setText(String.valueOf(tong));
    }//GEN-LAST:event_jButton1ActionPerformed

    private void btnTinhTichActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTinhTichActionPerformed
        // TODO add your handling code here:
        int so1 = Integer.valueOf(khongbiet.getText());
        int so2 = Integer.valueOf(txtSo2.getText());
        int tich = so1 * so2;
        txtTich.setText(String.valueOf(tich));
                
    }//GEN-LAST:event_btnTinhTichActionPerformed

